import { useMemo } from "react";
import { GameState } from "./classes/gameState";
import combatSystemPrompt from "./prompts/prompts";

const Battle = ({ playerOneWizard, playerTwoWizard, onReset }) => {
  const { snapshot, prompt } = useMemo(() => {
    const state = new GameState();
    state.initialize(playerOneWizard, playerTwoWizard);

    const battleSnapshot = state.battleSnapshot(0);
    const promptText = combatSystemPrompt(state.player_states[0]);

    return {
      snapshot: battleSnapshot,
      prompt: promptText,
    };
  }, [playerOneWizard, playerTwoWizard]);

  return (
    <section className="prompt-card">
      <button className="prompt-button button-outline" type="button" onClick={onReset}>
        Start Over
      </button>

      <h2 className="summary-title">Battle Snapshot</h2>
      <pre className="api-result__pre">{snapshot}</pre>

      <h2 className="summary-title">Combat System Prompt</h2>
      <pre className="api-result__pre">{prompt}</pre>
    </section>
  );
};

export default Battle;
