import { useState } from "react";
import "./App.css";

const API_BASE_URL =
  process.env.REACT_APP_API_BASE_URL ?? "http://localhost:3167";

function App() {
  const [prompt, setPrompt] = useState("");
  const [response, setResponse] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = async () => {
    if (!prompt.trim()) {
      setErrorMessage("Please ask a question before submitting.");
      return;
    }

    setErrorMessage("");
    setIsSubmitting(true);

    try {
      const res = await fetch(`${API_BASE_URL}/generate`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ prompt: prompt.trim() }),
      });

      if (!res.ok) {
        throw new Error(`Generation failed with status ${res.status}`);
      }

      const data = await res.json();
      setResponse(data);
    } catch (error) {
      setErrorMessage(
        error instanceof Error
          ? `Failed to generate a response. ${error.message}`
          : "Failed to generate a response."
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="app">
      <h1 className="title">Wizard Battle</h1>

      <section className="prompt-card">
        <label htmlFor="question-prompt" className="prompt-label">
          ask me a question
        </label>
        <textarea
          id="question-prompt"
          className="prompt-input"
          value={prompt}
          onChange={(event) => setPrompt(event.target.value)}
          placeholder="What would you like to know?"
        />
        <button
          className="prompt-button"
          onClick={handleSubmit}
          disabled={isSubmitting}
        >
          {isSubmitting ? "Generating..." : "Submit"}
        </button>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
      </section>

      {response && (
        <section className="api-result">
          <h2 className="summary-subtitle">Response</h2>
          <pre className="api-result__pre">
            {JSON.stringify(response, null, 2)}
          </pre>
        </section>
      )}
    </div>
  );
}

export default App;
