import { useMemo } from "react";
import { GameState } from "./classes/gameState";
import combatSystemPrompt from "./prompts/prompts";

const formatActions = (wizard, currentMana) => {
  const actions = wizard.affordable_actions(currentMana);
  return actions.map((action, index) => {
    const card = action.display_card?.();
    if (!card) {
      return null;
    }

    const type = String(card.type ?? "").toUpperCase();
    const manaCostLabel = `🔮 ${card.mana_cost ?? "?"}`;
    const emoji =
      type === "DAMAGE"
        ? "💥"
        : type === "BUFF"
        ? "📈"
        : type === "DEBUFF"
        ? "📉"
        : type === "HEAL"
        ? "🍃"
        : type === "DEFENSE"
        ? "🛡️"
        : "";

    const rangeLabel = (() => {
      if (type === "DEFENSE") {
        return "🛡️ 10%";
      }
      if (Array.isArray(card.range) && card.range.length === 2) {
        const [min, max] = card.range;
        return `${emoji} ${min}-${max}`.trim();
      }
      if (typeof card.range === "string") {
        return `${emoji} ${card.range}`.trim();
      }
      return emoji;
    })();

    const accuracyLabel = typeof card.accuracy === "number" ? `🎯 ${Math.round(card.accuracy * 100)}%` : "🎯 ?";
    const elementLabel = card.element ?? null;
    const elementClass = elementLabel ? `element-pill element-${String(elementLabel).toLowerCase()}` : null;
    const description = card.description ?? "";

    return (
      <div key={`${card.name}-${index}`} className={`spell-card ${type === "HEAL" ? "spell-card-heal" : type === "DEFENSE" ? "spell-card-defense" : "spell-card-spell"}`}>
        <div className="spell-card__row">
          <div className="spell-card__primary-left">
            {elementClass && <span className={elementClass}>{elementLabel}</span>}
            <span className="spell-card__name">{card.name}</span>
          </div>
          <div className="spell-card__primary-right">
            <span className="spell-card__meta spell-card__meta--range">{rangeLabel}</span>
            <span className="spell-card__meta">{accuracyLabel}</span>
            <span className="spell-card__meta">{manaCostLabel}</span>
          </div>
        </div>
        {description && (
          <div className="spell-card__row spell-card__row--description">
            <span className="spell-card__description">
              <em>{description}</em>
            </span>
          </div>
        )}
      </div>
    );
  });
};

const StatCircles = ({ current, max, color }) => {
  const clampedCurrent = Math.max(0, current);
  const clampedMax = Math.max(1, max);
  const percent = Math.max(0, Math.min(1, clampedCurrent / clampedMax));

  return (
    <div className="stat-circle" aria-label={`${clampedCurrent}/${clampedMax}`}>
      <div className="stat-circle__meter">
        <div
          className="stat-circle__fill"
          style={{
            backgroundColor: color,
            height: `${percent * 100}%`,
          }}
        />
        <span className="stat-circle__value">{clampedCurrent}</span>
      </div>
    </div>
  );
};

const HorizontalStatBars = ({ state }) => {
  const stats = [
    { key: "attack", label: "Attack", color: "#f87171" },
    { key: "defense", label: "Defense", color: "#60a5fa" },
    { key: "healing", label: "Healing", color: "#34d399" },
    { key: "arcane", label: "Arcane", color: "#c084fc" },
  ];

  return (
    <div className="stat-bars">
      {stats.map(({ key, label, color }) => (
        <div key={key} className="stat-row">
          <span className="stat-label">{label}</span>
          <div className="stat-meter">
            <div
              className="stat-meter__fill"
              style={{ width: `${Math.min(1, Math.max(0, state.player.wizard[key])) * 100}%`, backgroundColor: color }}
            />
          </div>
        </div>
      ))}
    </div>
  );
};

const BattleColumn = ({ actions, wizardState }) => (
  <section className="battle-column">
    <header className="battle-column__header">
      <h2>{wizardState.player.wizard.name}</h2>
    </header>
    <div className="battle-column__content">
      <div className="battle-stats">
        <div className="battle-stats__circles">
          <div className="battle-stats__circle-group">
            <span className="battle-stats__circle-label">Health</span>
            <StatCircles current={wizardState.current_health} max={wizardState.max_health} color="#f87171" />
          </div>
          <div className="battle-stats__circle-group">
            <span className="battle-stats__circle-label">Mana</span>
            <StatCircles current={wizardState.current_mana} max={25} color="#c084fc" />
          </div>
        </div>
        <HorizontalStatBars state={wizardState} />
      </div>
      <div className="battle-actions">
        <h4>Available Actions</h4>
        <div className="spell-cards">{actions}</div>
      </div>
    </div>
  </section>
);

const Battle = ({ playerOneWizard, playerTwoWizard, onReset }) => {
  const { snapshot, prompt, playerOneState, playerTwoState } = useMemo(() => {
    const state = new GameState();
    state.initialize(playerOneWizard, playerTwoWizard);

    const actingState = state.player_states[0];
    const enemyState = state.player_states[1];

    return {
      playerOneState: actingState,
      playerTwoState: enemyState,
      snapshot: state.battleSnapshot(0),
      prompt: combatSystemPrompt(actingState),
    };
  }, [playerOneWizard, playerTwoWizard]);

  return (
    <section className="battle-layout">
      <BattleColumn
        wizardState={playerOneState}
        actions={formatActions(playerOneState.player.wizard, playerOneState.current_mana)}
      />

      <section className="battle-log">
        <header className="battle-log__header">
          <h2>Battle Log</h2>
          <button className="prompt-button button-outline" type="button" onClick={onReset}>
            Start Over
          </button>
        </header>
        <div className="battle-log__content">
          <p>No actions yet.</p>
          <h3>Battle Snapshot</h3>
          <pre className="api-result__pre">{snapshot}</pre>
          <h3>Combat Prompt</h3>
          <pre className="api-result__pre">{prompt}</pre>
        </div>
      </section>

      <BattleColumn
        wizardState={playerTwoState}
        actions={formatActions(playerTwoState.player.wizard, playerTwoState.current_mana)}
      />
    </section>
  );
};

export default Battle;
