import { useState } from "react";
import "./App.css";

const API_BASE_URL =
  process.env.REACT_APP_API_BASE_URL ?? "http://localhost:3167";

function App() {
  const [phase, setPhase] = useState("player1");
  const [playerOneDraft, setPlayerOneDraft] = useState("");
  const [playerTwoDraft, setPlayerTwoDraft] = useState("");
  const [results, setResults] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handlePlayerOneSubmit = () => {
    if (!playerOneDraft.trim()) {
      setErrorMessage("Please describe Player 1's wizard before submitting.");
      return;
    }

    setErrorMessage("");
    setPhase("player2");
  };

  const updateResults = (label, updates) => {
    setResults((prev) => {
      const existing = prev.find((entry) => entry.label === label);
      if (existing) {
        return prev.map((entry) =>
          entry.label === label ? { ...entry, ...updates } : entry
        );
      }
      return [...prev, { label, ...updates }];
    });
  };

  const handlePlayerTwoSubmit = async () => {
    if (!playerTwoDraft.trim()) {
      setErrorMessage("Please describe Player 2's wizard before submitting.");
      return;
    }

    setErrorMessage("");
    setIsSubmitting(true);
    setResults([]);

    const descriptions = [
      { label: "Player 1", description: playerOneDraft.trim() },
      { label: "Player 2", description: playerTwoDraft.trim() },
    ];

    try {
      await Promise.all(
        descriptions.map(async ({ label, description }) => {
          const statsResponse = await fetch(`${API_BASE_URL}/generate_wizard_stats`, {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({ description }),
          });

          if (!statsResponse.ok) {
            throw new Error(`${label} wizard stats failed with status ${statsResponse.status}`);
          }

          const statsData = await statsResponse.json();
          updateResults(label, { stats: statsData });

          const spellsResponse = await fetch(`${API_BASE_URL}/generate_spells`, {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({ description, stats: statsData }),
          });

          if (!spellsResponse.ok) {
            throw new Error(`${label} spell generation failed with status ${spellsResponse.status}`);
          }

          const spellsData = await spellsResponse.json();
          updateResults(label, { spells: spellsData });
        })
      );
    } catch (error) {
      setErrorMessage(
        error instanceof Error
          ? `Failed to generate wizards or spells. ${error.message}`
          : "Failed to generate wizards or spells."
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="app">
      <h1 className="title">Wizard Battle</h1>

      {phase === "player1" && (
        <section className="prompt-card">
          <label htmlFor="player-one-prompt" className="prompt-label">
            Player 1, describe your wizard
          </label>
          <textarea
            id="player-one-prompt"
            className="prompt-input"
            value={playerOneDraft}
            onChange={(event) => setPlayerOneDraft(event.target.value)}
            placeholder="Describe your wizard..."
          />
          <button className="prompt-button" onClick={handlePlayerOneSubmit}>
            Submit
          </button>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
        </section>
      )}

      {phase === "player2" && (
        <section className="prompt-card">
          <label htmlFor="player-two-prompt" className="prompt-label">
            Player 2 describe your wizard
          </label>
          <textarea
            id="player-two-prompt"
            className="prompt-input"
            value={playerTwoDraft}
            onChange={(event) => setPlayerTwoDraft(event.target.value)}
            placeholder="Describe your wizard..."
          />
          <button
            className="prompt-button"
            onClick={handlePlayerTwoSubmit}
            disabled={isSubmitting}
          >
            {isSubmitting ? "Generating..." : "Submit"}
          </button>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
        </section>
      )}

      {!!results.length && (
        <section className="prompt-card">
          <h2 className="summary-title">Generated Wizards</h2>
          {results.map(({ label, stats, spells }) => (
            <div key={label} className="api-result">
              <h3 className="summary-subtitle">{label}</h3>
              {stats && (
                <>
                  <h4 className="summary-subtitle">Stats</h4>
                  <pre className="api-result__pre">
                    {JSON.stringify(stats, null, 2)}
                  </pre>
                </>
              )}
              {spells && (
                <>
                  <h4 className="summary-subtitle">Spells</h4>
                  <pre className="api-result__pre">
                    {JSON.stringify(spells, null, 2)}
                  </pre>
                </>
              )}
            </div>
          ))}
        </section>
      )}
    </div>
  );
}

export default App;
