import { useState } from "react";
import "./App.css";

function App() {
  const [step, setStep] = useState(1);
  const [playerOneDescription, setPlayerOneDescription] = useState("");
  const [playerTwoDescription, setPlayerTwoDescription] = useState("");

  const handleSubmit = () => {
    if (step === 1 && playerOneDescription.trim()) {
      setStep(2);
    }
  };

  return (
    <div className="app">
      <h1 className="title">Hello World</h1>

      {step === 1 ? (
        <section className="prompt-card">
          <label htmlFor="player-one-prompt" className="prompt-label">
            Player 1 enter your wizard description
          </label>
          <textarea
            id="player-one-prompt"
            className="prompt-input"
            value={playerOneDescription}
            onChange={(event) => setPlayerOneDescription(event.target.value)}
            placeholder="Describe your wizard..."
          />
          <button className="prompt-button" onClick={handleSubmit}>
            Submit
          </button>
        </section>
      ) : (
        <section className="prompt-card">
          <label htmlFor="player-two-prompt" className="prompt-label">
            Player 2 enter your wizard description
          </label>
          <textarea
            id="player-two-prompt"
            className="prompt-input"
            value={playerTwoDescription}
            onChange={(event) => setPlayerTwoDescription(event.target.value)}
            placeholder="Describe your wizard..."
          />
        </section>
      )}
    </div>
  );
}

export default App;
